import React, { useState } from "react";
import "./Recommender.css";

const Recommender = ({ userName }) => {
  const [formData, setFormData] = useState({
    lifestyle: "",
    sleep_style: "",
    back_pain: "",
    climate: ""
  });
  const [result, setResult] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      const res = await fetch("http://localhost:5000/recommend", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });
      const data = await res.json();
      setResult(data.match);
    } catch (err) {
      setResult("Error fetching recommendation.");
    }
  };

  return (
    <div className="recommender-container">
      <h1>üåô AI Mattress Recommender</h1>
      <p>Hi {userName}, here is your mattress match:</p>

      <select name="lifestyle" onChange={handleChange} value={formData.lifestyle}>
        <option value="">Select lifestyle</option>
        <option>Active</option>
        <option>Sedentary</option>
        <option>Athlete</option>
        <option>Combo</option>
      </select>

      <select name="sleep_style" onChange={handleChange} value={formData.sleep_style}>
        <option value="">Select sleep style</option>
        <option>Side</option>
        <option>Back</option>
        <option>Stomach</option>
        <option>Combo</option>
      </select>

      <select name="back_pain" onChange={handleChange} value={formData.back_pain}>
        <option value="">Back pain type</option>
        <option>None</option>
        <option>Lower</option>
        <option>Chronic</option>
      </select>

      <select name="climate" onChange={handleChange} value={formData.climate}>
        <option value="">Select climate</option>
        <option>Hot</option>
        <option>Cold</option>
        <option>Humid</option>
      </select>

      <button className="submit-button" onClick={handleSubmit}>
        Show My Mattress Match
      </button>

      {result && (
        <div className="result">
          <h2>üõèÔ∏è Recommended Mattress:</h2>
          <p><strong>{result}</strong></p>
        </div>
      )}
    </div>
  );
};

export default Recommender;
