import React, { useState } from 'react';
import './Login.css';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [name, setName] = useState('');
  const [mobile, setMobile] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || mobile.length !== 10 || !/^\d+$/.test(mobile)) {
      setError("Please enter a valid name and 10-digit mobile number.");
      return;
    }
    navigate('/dashboard', { state: { userName: name } });
  };

  return (
    <div className="login-page">
      <div className="moon">ðŸŒ™</div>
      <div className="login-box">
        <h2>Login to Mattress Recommender</h2>
        <input type="text" placeholder="Enter Name" value={name} onChange={(e) => setName(e.target.value)} />
        <input type="tel" placeholder="Mobile Number" value={mobile} onChange={(e) => setMobile(e.target.value)} />
        {error && <p className="error">{error}</p>}
        <button onClick={handleSubmit}>Continue</button>
      </div>
      <div className="footer">Created by Pritham Jujjavarapu ðŸŒŒ</div>
    </div>
  );
}
