import React, { useState } from "react";
import "./Recommender.css";

function Recommender() {
  const [lifestyle, setLifestyle] = useState("");
  const [sleepStyle, setSleepStyle] = useState("");
  const [backPain, setBackPain] = useState("");
  const [climate, setClimate] = useState("");
  const [recommendation, setRecommendation] = useState("");
  const username = localStorage.getItem("username") || "Guest";

  const handleSubmit = async () => {
    try {
      const response = await fetch("http://localhost:5000/recommend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          lifestyle,
          sleep_style: sleepStyle,
          back_pain: backPain,
          climate
        })
      });
      const data = await response.json();
      setRecommendation(data.match);
    } catch (error) {
      setRecommendation("Error fetching recommendation.");
    }
  };

  return (
    <div className="recommender">
      <h1>üåô AI Mattress Recommender</h1>
      <p>Hi {username}, here is your mattress match:</p>

      {/* Dropdowns */}
      <select value={lifestyle} onChange={(e) => setLifestyle(e.target.value)}>
        <option value="">Select Lifestyle</option>
        <option value="Active">Active</option>
        <option value="Sedentary">Sedentary</option>
        <option value="Athlete">Athlete</option>
        <option value="Combo">Combo</option>
      </select>

      <select value={sleepStyle} onChange={(e) => setSleepStyle(e.target.value)}>
        <option value="">Select Sleep Style</option>
        <option value="Back">Back</option>
        <option value="Side">Side</option>
        <option value="Stomach">Stomach</option>
        <option value="Combo">Combo</option>
      </select>

      <select value={backPain} onChange={(e) => setBackPain(e.target.value)}>
        <option value="">Select Back Pain Type</option>
        <option value="None">None</option>
        <option value="Lower">Lower</option>
        <option value="Chronic">Chronic</option>
      </select>

      <select value={climate} onChange={(e) => setClimate(e.target.value)}>
        <option value="">Select Climate</option>
        <option value="Hot">Hot</option>
        <option value="Cold">Cold</option>
        <option value="Humid">Humid</option>
        <option value="Dry">Dry</option>
      </select>

      {/* Submit Button */}
      <button onClick={handleSubmit}>Show My Mattress Match</button>

      {/* Result */}
      <h2>üõèÔ∏è Recommended Mattress:</h2>
      <p>{recommendation}</p>
    </div>
  );
}

export default Recommender;
