import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Dashboard.css';

export default function Dashboard() {
  const location = useLocation();
  const navigate = useNavigate();
  const userName = location.state?.userName || 'Guest';

  const handleHelpClick = () => {
    navigate('/recommend', { state: { userName } });
  };

  return (
    <div className="dashboard">
      <h1>üõèÔ∏è {userName}, here are your available mattresses</h1>
      <div className="mattress-grid">
        {[
          { title: "Hybrid AirFlow", desc: "Engineered for breathability..." },
          { title: "Soft Memory Foam", desc: "Contouring comfort..." },
          { title: "Latex Firm", desc: "Eco-friendly and responsive..." },
          { title: "Cloud Luxe Hybrid", desc: "Ultimate plush feel..." },
          { title: "EcoFlex Bamboo", desc: "Natural cooling..." },
          { title: "Orthopedic Support", desc: "Ergonomic support zones..." },
          { title: "Plush Gel Top", desc: "Gel-infused softness..." },
        ].map((mattress) => (
          <div className="mattress-card" key={mattress.title}>
            <h2>{mattress.title}</h2>
            <p>{mattress.desc}</p>
          </div>
        ))}
      </div>
      <button onClick={handleHelpClick}>Confused? Let us help you!</button>
    </div>
  );
}
