import React from "react";
import "./Dashboard.css";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const navigate = useNavigate();
  const username = localStorage.getItem("username") || "Guest";

  const handleRecommendClick = () => {
    navigate("/recommender");
  };

  const mattresses = [
    { name: "Hybrid AirFlow", desc: "Engineered for breathability..." },
    { name: "Soft Memory Foam", desc: "Contouring comfort..." },
    { name: "Latex Firm", desc: "Eco-friendly and responsive..." },
    { name: "Cloud Luxe Hybrid", desc: "Ultimate plush feel..." },
    { name: "EcoFlex Bamboo", desc: "Natural cooling..." },
    { name: "Orthopedic Support", desc: "Ergonomic support zones..." },
    { name: "Plush Gel Top", desc: "Gel-infused softness..." },
  ];

  return (
    <div className="dashboard">
      <h1>üõèÔ∏è {username}, here are your available mattresses</h1>
      <div className="mattress-grid">
        {mattresses.map((mattress, index) => (
          <div key={index} className="mattress-card">
            <h2>{mattress.name}</h2>
            <p>{mattress.desc}</p>
          </div>
        ))}
      </div>
      <button onClick={handleRecommendClick}>Confused? Let us help you!</button>
    </div>
  );
}

export default Dashboard;
